import React, { useEffect, useState } from "react";
import { View, Text, StyleSheet } from "react-native";
import Container from "../components/Container";
import { API } from "../services/API";
import { FlatList } from "react-native";
import { TouchableOpacity } from "react-native";
import { Ionicons } from "@expo/vector-icons";
import Error from "../components/Error";

const HomeScreen = ({ navigation }) => {
  //handle State
  const [todos, setTodos] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(false);

  //get all data todos
  const getTodos = async () => {
    try {
      setIsLoading(true);
      const result = await API.get("/todos");

      setTodos(result.data.data);
      setIsLoading(false);
      setError(false);

    } catch (error) {
      console.log(error);
      setError(true);
      setIsLoading(false);
    }
  };

  //handle update todo
  const handleUpdate = async (todo) => {
    try {
      const { id, status } = todo;

      const data = {
        status: status === "isDone" ? "notDone" : "isDone",
      };
      const result = await API.patch(`/todo/${id}`, data);

      setTodos(result.data.data.dataTodos);
    } catch (error) {
      console.log(error);
    }
  };

  //handle delete todos
  const handleDelete = async (todo) => {
    try {
      const { id } = todo;

      const result = await API.delete(`/todo/${id}`);

      setTodos(result.data.data.dataTodos);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    getTodos();
  }, []);


  const renderItem = ({ item }) => (
    <TouchableOpacity
      style={styles.fieldTodo}
      onPress={() => {
        handleUpdate(item);
      }}
    >
      {item.status === "isDone" ? (
        <>
          <Text style={styles.checkmark}>
            <Ionicons name="checkmark-outline" size={30} />
          </Text>
          <Text
            style={styles.isDone}
            numberOfLines={2}
            ellipsizeMode="tail"
          >
            {item.title}
          </Text>
          <TouchableOpacity
            onPress={() => {
              handleDelete(item);
            }}
          >
            <Ionicons name="close-outline" size={24} />
          </TouchableOpacity>
        </>
      ) : (
        <>
          <Text
            style={styles.notDone}
            numberOfLines={2}
            ellipsizeMode="tail"
          >
            {item.title}
          </Text>
        </>
      )}
    </TouchableOpacity>
  );

  return (
    <Container>
      {error ? (
        <Error getTodos={getTodos} />
      ) : (
        <View style={styles.content}>
          <FlatList
            data={todos}
            renderItem={_renderItem}
            keyExtractor={(item) => item.id}
            refreshing={isLoading}
            onRefresh={getTodos}
            style={{ height: "90%", flexGrow: 0 }}
          />

          <TouchableOpacity
            style={styles.plusButton}
            activeOpacity={0.5}
            onPress={() => {
              navigation.navigate("AddTodo");
            }}
          >
            <Ionicons name="duplicate-outline" color="#0080FF" size={70} />
          </TouchableOpacity>
        </View>
      )}
    </Container>
  );
};

const styles = StyleSheet.create({
  content: {
    flex: 1,
    backgroundColor: "#fff",
    width: "100%",
    padding: 20,
  },
  fieldTodo: {
    width: "100%",
    height: 50,
    borderColor: "#0080FF",
    borderWidth: 4,
    display: "flex",
    justifyContent: "center",
    paddingHorizontal: 10,
    marginBottom: 20,
    borderRadius: 10,
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
  },
  isDone: {
    textDecorationLine: "line-through",
    fontSize: 18,
    fontWeight: "bold",
    color: "#bdbdbd",
    width: "80%",
  },
  notDone: {
    fontSize: 18,
    fontWeight: "bold",
    color: "#212121",
    width: "90%",
  },
  plusButton: {
    position: "absolute",
    bottom: 20,
    right: 20,
  },
  checkmark: {
    color: "#3399FF",
  },
  plusText: {
    fontSize: 30,
    width: 70,
    height: 70,
    fontWeight: "bold",
    textAlign: "center",
    textAlignVertical: "center",
    transform: [{ scale: 2 }],
  },
});

export default HomeScreen;
