import React, { useState } from "react";
import {
  View,
  Text,
  StyleSheet,
  TouchableOpacity,
  TextInput,
} from "react-native";
import Container from "../components/Container";
import { Ionicons } from "@expo/vector-icons";
import { API } from "../services/API";
import { Button } from "react-native";

const AddTodoScreen = ({ navigation }) => {
  const [newTodo, setNewTodo] = useState("");
  const [isLoading, setIsLoading] = useState(true);

  const handleAdd = async () => {
    try {
      const data = {
        title: newTodo,
        status: "uncompleted",
      };
      setIsLoading(true)
      const result = await API.post("todo", data);
      setNewTodo("");
      navigation.goBack();
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <Container>
      <View style={styles.content}>
        <View style={styles.inputWrapper}>
          <TextInput
            style={styles.todoInput}
            maxLength={34}
            placeholder="What do you think?"
            value={newTodo}
            onChangeText={(val) => {
              setNewTodo(val);
            }}
          />
        </View>

        <TouchableOpacity>
          <IconButton
            icon={
              <Icon as={FontAwesome5} name="plus" size={4} color="blue.400" />
            }
            colorScheme="blue"
            ml={1}
            onPress={handleClick}
            mr={1}
          />
        </TouchableOpacity>
      </View>
    </Container>
  );
};

const styles = StyleSheet.create({
  content: {
    width: "100%",
    height: "100%",
    padding: 20,
  },
  inputWrapper: {
    flexDirection: "row",
    justifyContent: "space-between",
  },
  todoInput: {
    borderWidth: 2,
    borderColor: "#0080FF",
    height: 50,
    paddingHorizontal: 20,
    marginBottom: 20,
    borderRadius: 10,
    fontSize: 18,
    fontWeight: "bold",
    color: "#212121",
    width: "100%",
  },
  addButton: {
    width: "100%",
    justifyContent: "center",
    alignItems: "center",
    height: 50,
    borderRadius: 20
  },
});

export default AddTodoScreen;
